package com.debajoy.ds.tree.practice;

/**
 * @author Debajoy
 *
 */
public class SubStringTutorial {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "12345";
		System.out.println(s1.substring(0,1));
		System.out.println(s1.substring(1,2));
		System.out.println(s1.substring(2,3));
		System.out.println(s1.substring(3,4));
		System.out.println(s1.substring(4,5));
		System.out.println(s1.substring(4,5));

		System.out.println(Integer.parseInt(s1.substring(0,1)));
	}

}
